import React, { useState } from 'react';

const Reservation = () => {
    const [formData, setFormData] = useState({
        date: '',
        time: '',
        guests: '',
        message: ''
      });
    
      const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };
    
      const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here
        console.log(formData);
      };
    
  return (
    <div>
        <section className="container mx-auto p-10 grid grid-cols-1 md:grid-cols-2 gap-10">
      {/* Left Section - Reservation Form */}
      <div className="relative flex flex-col justify-center p-8 bg-gray-900 bg-opacity-50 text-white rounded-lg">
        <h3 className="text-red-500 text-lg font-semibold mb-2">Booking Table</h3>
        <h2 className="text-4xl font-bold mb-6">Make A Reservation</h2>

        {/* Form */}
        <div className="space-y-4">
          <div className="flex justify-between space-x-4">
            <input
              type="date"
              placeholder="Select Date"
              className="w-full bg-white text-gray-900 p-4 rounded-lg"
            />
            <input
              type="time"
              placeholder="Select Time"
              className="w-full bg-white text-gray-900 p-4 rounded-lg"
            />
          </div>
          <input
            type="number"
            placeholder="Total Guests"
            className="w-full bg-white text-gray-900 p-4 rounded-lg"
          />
          <textarea
            placeholder="Write Message"
            className="w-full bg-white text-gray-900 p-4 rounded-lg"
          ></textarea>
        </div>

        {/* Submit Button */}
        <button className="mt-6 bg-red-500 text-white py-3 px-6 rounded-lg hover:bg-red-600 transition">
          MAKE A RESERVATION
        </button>
      </div>

      {/* Right Section - Image */}
      <div className="relative">
        <img
          src="your-salad-image-url.jpg"
          alt="Salad"
          className="rounded-lg w-full h-full object-cover"
        />
      </div>
    </section>
    </div>
  )
}

export default Reservation
